#include <string>
#include "utils.h"

using namespace std;

// #define DEBUG

bool eq(const int a[], const int b[], int len) {
    for (int i = 0; i < len; i++) {
        if (a[i] != b[i])
            return false;
    }
    return true;
}

bool checkInclusion(const string &s1, string s2) {
    int m = s1.size();
    if (m > s2.size()) return false;
    int freq[26] = {0}, f[26] = {0};
    for (int i = 0; i < m; i++) {
        freq[s1[i] - 'a']++;
        f[s2[i] - 'a']++;
    }
    if (eq(freq, f, 26)) return true;

    for (int i = m; i < s2.size(); i++) {
#ifdef DEBUG
        cout << "[";
        for (int j: f) cout << j << " ";
        cout << "] ";
        cout << s2[i] << s2[i - m] << " ";
#endif
        f[s2[i] - 'a']++;
        f[s2[i - m] - 'a']--;
        if (eq(freq, f, 26))
            return true;
    }
    return false;
}

int main() {
    cout << checkInclusion("", "") << endl;
    cout << checkInclusion("", "a") << endl;
    cout << checkInclusion("a", "") << endl;
    cout << checkInclusion("ab", "a") << endl;
    cout << checkInclusion("adc", "adc") << endl;
    cout << checkInclusion("ab", "eidbaooo") << endl;
    cout << checkInclusion("ab", "eidboaoo") << endl;
    cout << checkInclusion("abb", "eidbaboo") << endl;
    cout << checkInclusion("adc", "dcda") << endl;
    cout << checkInclusion("wwkk", "pawwkkew") << endl;
    cout << measure([]() -> bool {
        return checkInclusion(
                "",
                "");
    }) << endl;
}